window.addEvent('domready', function() {

	var select 		= $('zid-picker');
	var button 		= $('get-result');
	var button_html	= button ? button.get('html') : '';
	var textarea	= $('code');
	var result		= $('result');
	var fields		= $('fields');

	if (textarea) {
		select.addEvent('change', function() {
			var data	= { zid: select.value, thispage_object: true };
			var url		= '/ajax/parsley-tester';
			fields.set('html', '');

			new Request.JSON({
				url: url,
				onSuccess: function(json) {
					json.each(function(val) {
						new Element('h6', { html: val }).inject(fields);
					});
				}
			}).post(data);
		});
		select.fireEvent('change');

		button.addEvents({
			mousedown: function() {
				this.addClass('running');
			},
			mouseup: function() {
				var data 	= { zid: select.value, parsley: textarea.value };
				var url		= '/ajax/parsley-tester';
				this.addClass('running');
				new Request({
					url: url,
					onComplete: function(response) {
						button.removeClass('running');
						result.set('html', response);
					}
				}).post(data);

				var data = { zlf: document.title, parsley: textarea.value };
				/*new Request({
					url: '/'
				}).post(data);*/
			}
		});

		textarea.addEvent('keydown', function(e) {
			if ((e.meta && e.key == 's') ||
				(e.meta && e.key == 'enter') ||
				(e.ctrl && e.key == 'enter')) {
				button.fireEvent('mouseup');
				return false;
			} else if (e.key == 'tab') {
				var pos = e.target.selectionStart;
            	e.target.value = e.target.value.substring(0, pos) + "    " + e.target.value.substring(pos);
            	e.target.setSelectionRange(pos + 4, pos + 4);
				return false;
			}
		});

		$('lesson-selector').value = window.location.pathname;
		$('lesson-selector').addEvent('change', function() {
			window.location = this.value;
		});


		if ($('move-back').get('data-current') == '1' && window.location.pathname == '/lesson-1/') $('move-back').destroy();
		if ($('move-forward').get('data-current') == $('lesson-selector').getChildren().pop().get('data-id')) $('move-forward').destroy();
	}

	new Tips('.tips', {
		className: 'tool-tip',
		onShow: function(tip, el) {
			var text = el.get('data-title');

			if (text && text.search('image:') == 0) {
				var src = text.substr(6);
				this.setText('<img src="' + src +'" />');
			}

			tip.setStyles({
                visibility: 'hidden',
                display: 'block'
            }).fade('in');
		}
	});
});",
